  data_cat2 = ohe_station9.transform(new_x_df[['GenHealth']])
  new_x_df = pd.concat([new_x_df.drop(columns=['GenHealth']),pd.DataFrame(data_cat2, columns=['GenHealth_' + str(col) for col in ohe_station9.categories_[0]])], axis=1)
  data_cat2 = ohe_station10.transform(new_x_df[['Asthma']])
